import React, { Component } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";
import Moment from "react-moment";

import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";

const token = localStorage.getItem("token");
// var userId = localStorage.getItem("userId");

class ViewCartOrder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orderitems: [],
    };
  }
  componentDidMount() {
    if (token == null) {
      this.props.history.push({
        pathname: "/signin",
      });
    } else {
      Axios.put("http://localhost:3001/api/my-cartvieworder", {
        orderid: this.props.location.state.orderid,
      }).then((res) => {
        const orderitems = res.data;

        this.setState({ orderitems });
      });
    }
  }

  render() {
    var cnt = 0;
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2"></div>
          <div
            className="col-md-8 p-2"
            style={{ marginBottom: "100px", border: "1px solid #84c225" }}
          >
            <div
              className="p-2 text-center text-"
              style={{ background: "#84c225", fontWeight: "bold" }}
            >
              
              Order details
            </div>
            <div>
              <table className="table table-bordered table-sm text-centered">
                <thead>
                  <tr>
                    <th width={10}>
                      <Link to="/" className="text-primary ">
                        <ArrowBackIcon />
                      </Link>
                    </th>
                    <th>
                      <Link
                        to="/my-list"
                        className="text-primary text-decoration-none d-flex flex-row-reverse"
                      >
                        <button
                          className="btn d-flex "
                          style={{
                            textDecoration: "none",
                            border: "1px solid #d9534f",
                            borderRadius: "5px",
                          }}
                        >
                          <img
                            src={require("../files/basket.png").default}
                            height={30}
                            width={30}
                            className="d-flex flex-row"
                            alt=""
                          />
                          <p>Basket</p>
                        </button>
                      </Link>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>
                      <ShoppingCartIcon style={{ color: "#84c225" }} />
                    </td>
                    <td className="p-1 text-success">My order</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div className="col-md-2"></div>
        </div>
      </div>
    );
  }
}

export default ViewCartOrder;
