var pp = localStorage.getItem("profilephoto");
var uname = localStorage.getItem("uname");
var umail = localStorage.getItem("umail");

const Account = () => {
  let history = useHistory();
  if (token == null) {
    history.push({
      pathname: "/signin",
    });
  } else {
