import React,{Component} from 'react';
import AddIcon from '@material-ui/icons/Add';
import Axios from 'axios';
import { Link } from 'react-router-dom';
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
import CreateIcon from '@material-ui/icons/Create';
import "./ViewProducts.css";

class ViewProducts extends Component{
      constructor(props){
    super(props)  
  this.state={
   products: []
  }
} 
 componentDidMount() {
Axios.get("http://localhost:3001/view-prod")
      .then(res => {
        const products = res.data;
        this.setState({ products });
      })
 }

 render(){
  const {}=this.state;
 var cnt=0;
    return (
        <div className="container" style={{marginBottom:"100px"}}>
        <div className="row">
            <div className="col-md-2"></div>
            <div className="col-md-7"  style={{border:"1px solid #84c225",paddingTop:"10px"}}>
            <h5 className="alert alert-info" style={{fontWeight:"bolder",textDecoration:"underline"}}>
            VIEW PRODUCT 
            <Link to="/add-prod" className="btn btn-primary" sty>
           <AddIcon />
           Add Product</Link></h5>
           
            <table className="table table-bordered table-responsive table-xl text-centered">
      <thead>
      <tr>  
        <th className="text-success"></th>
        <th className="bg-success text-light">NAME</th>
        <th className="bg-success text-light">INFO</th>
        <th className="bg-success text-light">CATEGORY</th>
        <th className="bg-success text-light">PRICE</th>
        <th className="bg-success text-light">M</th>
        <th className="bg-info text-light">Action
        </th>
       
        
      </tr>
    </thead>
    <tbody>
    
               
               
                
                {
                   
                  this.state.products.map(product =>
                     
                <tr>
                <td>{cnt=cnt+1}</td>
                <td style={{fontWeight:'bold'}}>{product.prodName}
                <img src={require('../files/'+product.prodPic).default} height={100} width={100} />
                </td>
                <td>{product.prodDesc}</td>
                <td>{product.prodCategory}</td>
                <td>{product.prodPrice}</td>
                <td>{product.prodUOM}</td>
                <td>
                <Link to="/update-prod" className="btn btn-warning mb-2"><CreateIcon /></Link>
                <Link to="/del-prod" className="btn btn-danger"><DeleteOutlineIcon /></Link>
                </td>
                </tr>)
                }
                
               

      </tbody>
    </table>
    

            </div>
            <div className="col-md-3"></div>
        </div>
      
   
        </div>
   );
}
};

export default ViewProducts
