import React,{Component} from 'react';
import "./Home.css";
import Axios from 'axios';

var token = localStorage.getItem('token');
var pp = localStorage.getItem('profilephoto');
  
class Home extends Component{
  constructor(props){
super(props)  
this.state={
products: [],
count: 1
}
} 
componentDidMount() {
  Axios.get("http://localhost:3001/api/view-prod")
        .then(res => {
          const products = res.data;
          this.setState({ products });
        })
   }

incrementQty=(e)=>{
  this.setState({
    count: this.state.count + 1
  });
}
decrementQty=(e)=>{
  if(this.state.count===1)
  {
    this.setState({
      count: this.state.count
    });
  }
  else
  {
    this.setState({
      count: this.state.count - 1
    });
  }
  
}  
  
  render(){
    

  return (
    <div className="container home" style={{marginBottom:"100px",border:"1px solid #84c225"}}>
        <div className="row">
            <div className="col-sm productbox d-flex"  style={{paddingTop:"10px",paddingBottom:"10px"}}>
             
            <table className="d-flex felx-column">
            {  this.state.products.map(product =>

             <div className="p-3 " style={{borderRadius:"10px",boxShadow:"0 0 10px gray",marginRight:"20px",width:"180px"}}>
              <div className="text-center mb-3" style={{borderRadius:"10px",boxShadow:"0 0 5px gray"}}>
                <img height={150} width={150} />
              </div>
              <div className="">  
              <p style={{fontWeight:"bold"}}>{product.prodName}</p>
              <p className="alert alert-secondary">{product.prodUOM}</p>
              <div style={{display:"flex"}}>
              <p style={{marginRight:"5px"}}>Rs</p><p style={{fontWeight:"bold"}}>
                {product.prodPrice}
              </p> </div>
              </div>
              <div className="d-flex justify-content-center">
              <div className="text-center p-0" style={{display:"flex"}}>

              <input type="button" onClick={this.decrementQty}  style={{padding:"0px 11px",borderRadius:"5px"}} className="btn-light text-danger" value="-" />

              <input type="text"  min="1" max="" name="prodQty" value= {this.state.count} title="Qty"  className="bg-white text-center text-danger form-control" style={{width:"50px",border:"1px solid #ba5253"}} />

              <input type="button" onClick={this.incrementQty} style={{padding:"0px 10px",borderRadius:"5px"}} className="btn-light text-danger" value="+" />
              
              </div>
              </div>
              <div className="d-flex justify-content-center">
                 <input id="addc" style={{border:"1px solid #ba5253",borderRadius:"2px",width:"100px",background:"#d9534f"}} className="mt-3 text-light" type="submit" value="Add" />
                
              </div>
             </div>
            )
            }
            </table>
            </div>
        </div>   

    </div>
);
}
};


export default Home;
