import React, { Component } from "react";
import ListAltIcon from "@material-ui/icons/ListAlt";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
import { Link } from "react-router-dom";

import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Axios from "axios";
var userid = localStorage.getItem("userId");

const token = localStorage.getItem("token");

class ProdCheckout extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  remProdFromList = (id) => {
    //console.log(id);
    Axios.post("http://localhost:3001/api/remlist-prod", {
      upNo: id,
    });
  };
  render() {
    var cnt = 0;
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2"></div>
          <div
            className="col-md-8 p-2"
            style={{ marginBottom: "100px", border: "1px solid #84c225" }}
          >
            <div
              className="p-2 text-center "
              style={{ background: "#84c225", fontWeight: "bold" }}
            >
              <ShoppingCartIcon className="text-success" /> Order Checkout
            </div>

            <div>
              <table className="table table-bordered table-sm text-centered">
                <thead>
                  <tr>
                    <th width={10}>
                      <Link to="/my-list" className="text-primary ">
                        <ArrowBackIcon className="text-danger" />
                      </Link>
                    </th>
                    <th colSpan="2"></th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>
                      <ShoppingCartIcon />
                    </td>
                    <td
                      colSpan="2"
                      style={{ background: "#DCDCDC" }}
                      className="p-1"
                    >
                      GIMS Order
                    </td>
                  </tr>
                  <tr>
                    <td></td>

                    <td className="d-flex"></td>
                    <td className="text-center pt-5">
                      <DeleteOutlineIcon />
                    </td>
                  </tr>

                  <tr>
                    <td></td>
                    <td colSpan="2">
                      <p className="alert alert-danger">
                        Total
                        <p className="text-danger">Rs </p>
                      </p>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div className="col-md-2"></div>
        </div>
      </div>
    );
  }
}

export default ProdCheckout;
