import React, { Component } from "react";
import ListAltIcon from "@material-ui/icons/ListAlt";
import DeleteOutlineIcon from "@material-ui/icons/DeleteOutline";
import { Link } from "react-router-dom";

import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Axios from "axios";
var userid = localStorage.getItem("userId");

const token = localStorage.getItem("token");

class ProdCheckout extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  remProdFromList = (id) => {
    //console.log(id);
    Axios.post("http://localhost:3001/api/remlist-prod", {
      upNo: id,
    });
  };
  render() {
    var cnt = 0;
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2"></div>
          <div
            className="col-md-8 p-2"
            style={{ marginBottom: "100px", border: "1px solid #84c225" }}
          >
            <div
              className="p-2 text-center"
              style={{ background: "#84c225", fontWeight: "bold" }}
            >
              <ShoppingCartIcon /> Order Checkout
            </div>

            <div>
              <table className="table table-bordered table-sm text-centered">
                <thead>
                  <tr>
                    <th width={10}>
                      <Link to="/my-list" className="text-primary ">
                        <ArrowBackIcon />
                      </Link>
                    </th>
                    <th colSpan="2"></th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>
                      <Sho />
                    </td>
                    <td
                      colSpan="2"
                      style={{ background: "#DCDCDC" }}
                      className="p-1"
                    >
                      My List
                    </td>
                  </tr>
                  {this.state.products.map((product) => (
                    <tr>
                      <td>{(cnt = cnt + 1)}</td>

                      <td className="d-flex">
                        <div style={{ width: "120px" }}>
                          <img
                            src={require("../files/" + product.prodPic).default}
                            height={100}
                            width={100}
                          />
                          <p style={{ fontWeight: "bold" }}>
                            {product.prodName}
                          </p>
                        </div>
                        <div style={{ marginLeft: "30px" }}>
                          <p>{product.prodDesc}</p>
                          <p style={{ fontWeight: "bold" }} className="d-flex">
                            Quantity-
                            <p className="text-danger">{product.upQty}</p>
                          </p>
                          <p style={{ fontWeight: "bold" }} className=" d-flex">
                            <p className="text-dark">Rs.</p>
                            <p className="text-secondary">
                              {product.prodPrice}
                            </p>
                            <p style={{ marginLeft: "5px" }}>X</p>
                            <p
                              style={{ marginLeft: "5px" }}
                              className="text-danger"
                            >
                              {product.upQty}
                            </p>
                            <p style={{ marginLeft: "5px" }}>=</p>
                            <p
                              style={{ marginLeft: "5px" }}
                              className="text-success"
                            >
                              {(ptot = product.prodPrice * product.upQty)} Rs
                              <p hidden>{(totprice += ptot)}</p>
                            </p>
                          </p>
                        </div>
                      </td>
                      <td className="text-center pt-5">
                        <button
                          className="btn btn-danger"
                          onClick={() => this.remProdFromList(product.upNo)}
                        >
                          <DeleteOutlineIcon />
                        </button>
                      </td>
                    </tr>
                  ))}
                  <tr>
                    <td></td>
                    <td colSpan="2">
                      <p className="alert alert-danger">
                        Total
                        <p className="text-danger">Rs {totprice}</p>
                        <Link
                          to={{
                            pathname: "/checkout",
                            state: { totalprice: totprice, custId: userid },
                          }}
                          className="btn btn-success"
                        >
                          CheckOut <ShoppingCartIcon />
                        </Link>
                      </p>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div className="col-md-2"></div>
        </div>
      </div>
    );
  }
}

export default ProdCheckout;
