import React, { Component } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";
import Moment from "react-moment";
import ArrowDropDownCircleIcon from "@material-ui/icons/ArrowDropDownCircle";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";

const token = localStorage.getItem("admintoken");

class ViewCustOrders extends Component {
  constructor(props) {
    super(props);
    this.state = {
      custorders: [],
    };
  }
  componentDidMount() {
    if (token == null) {
      this.props.history.push({
        pathname: "/adminlogin",
      });
    } else {
      Axios.put("http://localhost:3001/api/view-custorders").then((res) => {
        const orders = res.data;
        if (orders.length === 0) {
          this.setState({ emptyorderlist: true });
        }

        this.setState({ orders });
      });
    }
  }

  viewOrder = (id, odate, dadd, name, phone, totalprice) => {
    console.log(id);

    this.props.history.push({
      pathname: "/view-cartorder",
      state: {
        orderid: id,
        orderdate: odate,
        deliveryadd: dadd,
        name: name,
        phone: phone,
        ototalprice: totalprice,
      },
    });
  };

  render() {
    var cnt = 0;
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2"></div>
          <div
            className="col-md-8 p-2"
            style={{ marginBottom: "100px", border: "1px solid #84c225" }}
          >
            <div
              className="p-2 text-center text-dark"
              style={{ background: "#84c225", fontWeight: "bold" }}
            >
              <ShoppingCartIcon style={{ color: "green" }} />
              Customer Orders
            </div>
            <div
              className="p-2 d-flex flex-row-reverse"
              style={{ fontWeight: "bold" }}
            >
              <Moment format="DD/MM/YYYY">{new Date().toLocaleString()}</Moment>
            </div>
            <table className="table table-bordered  table-sm text-centered">
              <thead>
                <tr>
                  <th width={10}>O.No</th>
                  <th>Customer Name</th>
                  <th>Mobile</th>
                  <th>Delivery Address</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>gismo11</td>
                  <td>Nilesh</td>
                  <td>8975751828</td>
                  <td>Narhe Pune</td>
                  <td className="p-2">
                    <button className="btn btn-danger m-2">View</button>
                    <select
                      name="orderstatus"
                      style={{ width: "80px" }}
                      className="form-control"
                      required
                    >
                      <option value="CUSTOMER"></option>
                      <option value="RETAILER">SHOP KEEPER</option>
                    </select>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <div className="col-md-2"></div>
        </div>
      </div>
    );
  }
}

export default ViewCustOrders;
