import React, { Component } from "react";
import { Link } from "react-router-dom";

import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import PlaylistAddIcon from "@material-ui/icons/PlaylistAdd";
import ListAltIcon from "@material-ui/icons/ListAlt";

const token = localStorage.getItem("token");

class MyCart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      itemcount: "",
      display: false,
    };
  }
  componentDidMount() {
    if (token == null) {
      this.props.history.push({
        pathname: "/signin",
      });
    }
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2"></div>
          <div
            className="col-md-8 p-2"
            style={{ marginBottom: "100px", border: "1px solid #84c225" }}
          >
            <div
              className="p-2 text-center "
              style={{ background: "#84c225", fontWeight: "bold" }}
            >
              <ShoppingCartIcon className="text-success" />
              My Order
            </div>
            <div>
              <table className="table table-bordered table-sm text-centered">
                <thead>
                  <tr>
                    <th width={10}>
                      <Link to="/" className="text-primary ">
                        <ArrowBackIcon />
                      </Link>
                    </th>
                    <th colSpan="2">
                      <Link
                        to="/my-list"
                        className="text-primary text-decoration-none d-flex flex-row-reverse"
                      >
                        <button
                          className="btn "
                          style={{
                            textDecoration: "none",
                            border: "1px solid #d9534f",
                            borderRadius: "5px",
                          }}
                        >
                          <img
                            src={require("../files/basket.png").default}
                            height={30}
                            width={30}
                            className="d-flex flex-row"
                          />
                          <p>Basket</p>
                        </button>
                      </Link>
                    </th>
                  </tr>
                </thead>
                {this.state.emptylist ? (
                  <tbody>
                    <tr>
                      <td>
                        <ListAltIcon />
                      </td>
                      <td
                        colSpan="2"
                        style={{ background: "#DCDCDC" }}
                        className="p-1"
                      >
                        My List
                      </td>
                    </tr>
                    <tr>
                      <td></td>
                      <td>
                        <div className="text-danger">
                          <img
                            src={require("../files/basket.png").default}
                            height={30}
                            width={30}
                          />
                          Your Basket List is Empty
                        </div>

                        <Link to="/" className="text-primary ">
                          <button className="btn btn-success">
                            <PlaylistAddIcon /> Create List
                          </button>
                        </Link>
                      </td>
                    </tr>
                  </tbody>
                ) : (
                  <tbody>
                    <tr>
                      <td>
                        <ListAltIcon />
                      </td>
                      <td
                        colSpan="2"
                        style={{ background: "#DCDCDC" }}
                        className="p-1"
                      >
                        My List
                      </td>
                    </tr>
                  </tbody>
                )}
              </table>
            </div>
          </div>
          <div className="col-md-2"></div>
        </div>
      </div>
    );
  }
}

export default MyCart;
