import React,{Component} from 'react';
import AddIcon from '@material-ui/icons/Add';
import Axios from 'axios';

import "./AddProducts.css";

class AddProducts extends Component{
      constructor(props){
    super(props)  
  this.state={
  pname:"",
  pdesc:"",
  pcat:"",
  pprice:"",
  puom:"",
  ppic:"",
  error:{}
  }
} 
onChanges= (e) => {
  this.setState({[e.target.name]: e.target.value});
}

formVal=()=>{
  const {pname,pdesc,address,state,city,pin,errors,redirect}=this.state;
  
  let isValid=true;
  if(!Number(mobileno)){
    errors.mnoerr="*mobile no must be digits!";
    isValid=false;
   }
  else if(mobileno.length !== 10){
   errors.mnoerr="*mobile no is not valid!";
   isValid=false;
  }
  if(address.length < 5){
   errors.addrerr="*Address is too short!";
   isValid=false;
  }
  if(!String(state)){
    errors.stateerr="*State is not valid!";
    isValid=false;
   }
  else if(state.length < 4 ){
   errors.stateerr="*State is Invalid!";
   isValid=false;
  }
  if(!String(city)){
    errors.cityerr="*City is not valid!";
    isValid=false;
   }
  else if(city.length < 4 ){
   errors.cityerr="*City is Invalid!";
   isValid=false;
  }
  if(!Number(pin)){
    errors.pinerr="*pin code must be digits!";
    isValid=false;
   }
  else if(pin.length !== 6){
   errors.pinerr="*pin code is not valid!";
   isValid=false;
  }
  this.setState({errors});
  return isValid;
}



onAdd=(e)=>{
  e.preventDefault();
  
  
  const isValid=this.formVal();
  if(isValid){
  
    
    Axios.post("http://localhost:3001/api/add-prod",{
      name:this.state.pname,
      pdesc:this.state.pdesc,
      pcat:this.state.pcat,
      pprice:this.state.pprice,
      puom:this.state.puom,
      ppic:this.state.ppic
        
    })
    this.props.history.push('/add-prod');
    alert("New Product Added !");
  }
  
}

 render(){
  const {pname,pdesc,pcat,pprice,puom,ppic}=this.state;
 
    return (
        <div className="container" style={{marginBottom:"100px"}}>
        <div className="row">
            <div className="col-md-3"></div>
            <div className="col-md-6"  style={{border:"1px solid #84c225",paddingTop:"10px"}}>
            <h5 className="alert alert-info" style={{fontWeight:"bolder",textDecoration:"underline"}}>
            ADD PRODUCT</h5>
            <form onSubmit={this.onAdd}>
            <table className="table table-bordered table-responsive table-xl text-centered">
      <thead>
      <tr>  
        <th className="text-success"></th>
        <th className="bg-success text-light">PRODUCT INFO</th>
        <th className="bg-success text-light">Enter Details</th>
        
      </tr>
    </thead>
    <tbody>
     
      <tr>
      <td className="text-success">1</td>
      <td className="pinfo">Product Name</td>
      <td>
      <input
            className="form-control"
            type='text'
            name='pname'
            placeholder='Product Name'
            onChange={this.onChanges}
            required/>
      </td>
      </tr>
      <tr>
      <td className="text-success">2</td>
      <td className="pinfo">Product Description</td>
      <td>
      <textarea
            className="form-control"

            rows="3"
            name='pdesc
            
            '
            placeholder="Product Description"
           onChange={this.onChanges}
            required/>
      </td>
      </tr>
      <tr>
      <td className="text-success">3</td>
      <td className="pinfo">Product Category</td>
      <td>
      <select className="form-select" name="pcat" onChange={this.onChanges} required>
            <option value="">Select Category</option>
            <option value="Atta,Flours & Souji">Atta,Flours & Souji</option>
            <option value="Rice Product">Rice Product</option>
            <option value="Salt,Sugar & Jaggery">Salt,Sugar & Jaggery</option>
            <option value="Dairy">Dairy</option>
            <option value="Stationary">Stationary</option>
            <option value="Breads">Breads</option>
            <option value="Bath">Bath</option>
            <option value="Chocolates & Biscuits">Chocolates & Biscuits</option>
            <option value="Cookies & Khari">Cookies & Khari</option>
            <option value="Foodgrains, Oil & Masala">Foodgrains, Oil & Masala</option>
            <option value="Backery & Dairy">Backery & Dairy</option>
            <option value="Beverage">Beverage</option>
            <option value="Snacks">Snacks</option>
            <option value="Cleaning & HouseHold">Cleaning & HouseHold</option>
            <option value="Gourment & world food">Gourment & world food</option>
            <option value="Baby Care">Baby Care</option>
        </select>
       </td>
      </tr>
      <tr>
      <td className="text-success">4</td>
      <td className="pinfo">Product Price</td>
      <td>
      <input
            className="form-control"
            type='text'
            name='pprice'
            placeholder='Product Price (in rupees)'
            onChange={this.onChanges}
            required/>(in rupees)
      </td>
      </tr>
      <tr>
      <td className="text-success">5</td>
      <td className="pinfo">Product UOM<br/>(Unit of Measurement)</td>
      <td>
      <select className="form-select" name="puom" onChange={this.onChanges} required>
            <option value="">Select UOM</option>
            <option value="g"> g</option>
            <option value="kg">kg</option>
            <option value="l"> l</option>
            <option value="ml">ml</option>
           <option value="pcs">pcs-piece</option>
            
           </select>
       </td>
      </tr>
      <tr>
      <td className="text-success">6</td>
      <td className="pinfo">Product picture</td>
      <td>
      <input 
           type="file" 
           name="ppic"
           class="form-control-file" 
           id="exampleFormControlFile1" 
           onChange={this.onChanges}
           required
           />
      </td>
      </tr>
      <tr>
      <td></td>
      <td>
      <button class="btn btn-success btn-block" type="submit">Add</button>
      </td>
      <td>
      <input class="btn btn-danger" type="reset" value="Reset" />
      </td>
      </tr>
      </tbody>
    </table>
    </form>


            </div>
            <div className="col-md-3"></div>
        </div>
      
   
        </div>
   );
}
};

export default AddProducts
