import React, { Component } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";

import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";

const token = localStorage.getItem("token");
var userId = localStorage.getItem("userId");

class MyCart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orders: [],
      itemcount: "",
      emptyorder: false,
    };
  }
  componentDidMount() {
    if (token == null) {
      this.props.history.push({
        pathname: "/signin",
      });
    } else {
      Axios.put("http://localhost:3001/api/my-orders", { id: userId }).then(
        (res) => {
          const orders = res.data;
          if (orders.length === 0) {
            this.setState({ emptyorder: true });
          }

          this.setState({ orders });
        }
      );
    }
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2"></div>
          <div
            className="col-md-8 p-2"
            style={{ marginBottom: "100px", border: "1px solid #84c225" }}
          >
            <div
              className="p-2 text-center "
              style={{ background: "#84c225", fontWeight: "bold" }}
            >
              <ShoppingCartIcon className="text-success" />
              My Order
            </div>
            <div>
              <table className="table table-bordered table-sm text-centered">
                <thead>
                  <tr>
                    <th width={10}>
                      <Link to="/" className="text-primary ">
                        <ArrowBackIcon />
                      </Link>
                    </th>
                    <th>
                      <Link
                        to="/my-list"
                        className="text-primary text-decoration-none d-flex flex-row-reverse"
                      >
                        <button
                          className="btn d-flex "
                          style={{
                            textDecoration: "none",
                            border: "1px solid #d9534f",
                            borderRadius: "5px",
                          }}
                        >
                          <img
                            src={require("../files/basket.png").default}
                            height={30}
                            width={30}
                            className="d-flex flex-row"
                            alt=""
                          />
                          <p>Basket</p>
                        </button>
                      </Link>
                    </th>
                  </tr>
                </thead>

                <tbody>
                  <tr>
                    <td>
                      <ShoppingCartIcon style={{ color: "#84c225" }} />
                    </td>
                    <td className="p-1 text-success">My order</td>
                  </tr>
                  <tr>
                    <td>1</td>
                    <td className="d-flex">
                      <div className="container border border-success rounded ">
                        <div className="row">
                          <div className="col-md-2 p-2">
                            <ShoppingCartIcon
                              style={{
                                fontSize: "50px",
                                color: "#84c225",
                              }}
                            />
                          </div>
                          <div className="col-md-5 p-2">
                            <p
                              className="d-flex"
                              style={{ fontWeight: "bold", color: "green" }}
                            >
                              GIMS order on
                              <p
                                className="text-danger"
                                style={{ marginLeft: "5px" }}
                              >
                                22/01/2021
                              </p>
                            </p>
                            <p
                              className="d-flex rounded text-danger"
                              style={{
                                height: "30px",
                                width: "100px",
                                fontWeight: "bold",
                                paddingLeft: "5px",
                                background: "#84c225",
                              }}
                            >
                              <p className="text-dark">Rs.</p>310
                            </p>
                          </div>
                          <div className="col-md-5 p-2">
                            <div className="p-1">
                              <button
                                className="btn btn-warning "
                                style={{ marginRight: "15px" }}
                              >
                                order status
                              </button>
                              <button className="btn btn-success">
                                order details
                                <ArrowForwardIosIcon
                                  style={{ fontSize: "12px" }}
                                />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
          <div className="col-md-2"></div>
        </div>
      </div>
    );
  }
}

export default MyCart;
