import React,{Component} from 'react';
import {withRouter} from 'react-router-dom';

import "./AuthRegister.css";
import Axios from 'axios';
import { Redirect } from "react-router-dom";
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';

class AuthRegisterfb extends Component {
    constructor(props){
        super(props)  
      this.state={
      mobileno:"",
      address:"",
      state:"",
      city:"",
      pin:"",
      role:"",
      errors:{}
      }
    } 
    componentDidMount() {
      const { history } = this.props;
       window.addEventListener("popstate", () => {
         
       history.push('/account-login');
     });
     }
    onChange= (e) => {
       this.setState({[e.target.name]: e.target.value});
    }
    
    formValidation=()=>{
      const {role,mobileno,address,state,city,pin,errors,redirect}=this.state;
      
      let isValid=true;
      if(!Number(mobileno)){
        errors.mnoerr="*mobile no must be digits!";
        isValid=false;
       }
      else if(mobileno.length !== 10){
       errors.mnoerr="*mobile no is not valid!";
       isValid=false;
      }
      if(address.length < 5){
       errors.addrerr="*Address is too short!";
       isValid=false;
      }
      if(!String(state)){
        errors.stateerr="*State is not valid!";
        isValid=false;
       }
      else if(state.length < 4 ){
       errors.stateerr="*State is Invalid!";
       isValid=false;
      }
      if(!String(city)){
        errors.cityerr="*City is not valid!";
        isValid=false;
       }
      else if(city.length < 4 ){
       errors.cityerr="*City is Invalid!";
       isValid=false;
      }
      if(!Number(pin)){
        errors.pinerr="*pin code must be digits!";
        isValid=false;
       }
      else if(pin.length !== 6){
       errors.pinerr="*pin code is not valid!";
       isValid=false;
      }
      this.setState({errors});
      return isValid;
    }
    
    onSubmit=(e)=>{
      e.preventDefault();
      
      
      const isValid=this.formValidation();
      if(isValid){
        
        Axios.post("http://localhost:3001/api/g-user",{
          name:this.props.location.state.name,
          photolink:this.props.location.state.photolink,
          email:this.props.location.state.email,
          googleId:this.props.location.state.googleId,
          mobileno:this.state.mobileno,
          address:this.state.address,
          state:this.state.state,
           city:this.state.city, 
           pin:this.state.pin,
           role:this.state.role
            
        })
        this.props.history.push('/signin');
        alert("Your Registeration sucess ! Please Signup & Continue shopping.");
      }
      
    }
    
    render() {
        return (
            <div>
                
            </div>
        )
    }
}
export default withRouter(AuthRegister);
